import React, { useState, useEffect } from "react";
import axios from "axios";

function WeatherApp() {
  const [city, setCity] = useState("Paris");
  const [weatherData, setWeatherData] = useState({});
  const [forecastData, setForecastData] = useState([]);

  useEffect(() => {
    searchCity(city);
  }, [city]);

  function refreshWeather(response) {
    const data = response.data;
    setWeatherData({
      temperature: Math.round(data.temperature.current),
      city: data.city,
      description: data.condition.description,
      humidity: data.temperature.humidity,
      windSpeed: data.wind.speed,
      time: formatDate(new Date(data.time * 1000)),
      iconUrl: data.condition.icon_url,
    });

    getForecast(data.city);
  }

  function getForecast(city) {
    const apiKey = "b2a5adcct04b33178913oc335f405433";
    const apiUrl = `https://api.shecodes.io/weather/v1/forecast?query=${city}&key=${apiKey}&units=metric`;

    axios.get(apiUrl).then((response) => {
      const forecast = response.data.daily.slice(0, 5).map((day) => ({
        date: formatDay(day.time),
        maxTemp: Math.round(day.temperature.maximum),
        minTemp: Math.round(day.temperature.minimum),
        iconUrl: day.condition.icon_url,
      }));
      setForecastData(forecast);
    });
  }

  function formatDate(date) {
    let minutes = date.getMinutes();
    let hours = date.getHours();
    let days = ["Sunday", "Monday", "Tuesday", "Wednesday", "Thursday", "Friday", "Saturday"];
    let day = days[date.getDay()];

    if (minutes < 10) {
      minutes = `0${minutes}`;
    }

    return `${day} ${hours}:${minutes}`;
  }

  function formatDay(timestamp) {
    let date = new Date(timestamp * 1000);
    let days = ["Sun", "Mon", "Tue", "Wed", "Thu", "Fri", "Sat"];
    return days[date.getDay()];
  }

  function searchCity(city) {
    const apiKey = "b2a5adcct04b33178913oc335f405433";
    const apiUrl = `https://api.shecodes.io/weather/v1/current?query=${city}&key=${apiKey}&units=metric`;
    axios.get(apiUrl).then(refreshWeather);
  }

  function handleSearchSubmit(event) {
    event.preventDefault();
    const searchInput = event.target.elements.city.value;
    setCity(searchInput);
  }

  return (
    <div className="weather-app">
      <header>
        <form className="search-form" id="search-form" onSubmit={handleSearchSubmit}>
          <input type="search" placeholder="Enter a city..." required id="search-form-input" name="city" className="search-form-input" />
          <input type="submit" value="Search" className="search-form-button" />
        </form>
      </header>
      <main>
        <div className="weather-app-data">
          <div>
            <h1 className="weather-app-city">{weatherData.city}</h1>
            <p className="weather-app-details">
              <span>{weatherData.time}</span>, <span>{weatherData.description}</span>
              <br />
              Humidity: <strong>{weatherData.humidity}</strong>%, Wind: <strong>{weatherData.windSpeed}</strong> km/h
            </p>
          </div>
          <div className="weather-app-temperature-container">
            <div id="icon">
              <img src={weatherData.iconUrl} className="weather-app-icon" alt={weatherData.description} />
            </div>
            <div className="weather-app-temperature" id="temperature">{weatherData.temperature}</div>
            <div className="weather-app-unit">°C</div>
          </div>
        </div>
        <div className="weather-forecast" id="forecast">
          {forecastData.map((day, index) => (
            <div className="weather-forecast-day" key={index}>
              <div className="weather-forecast-date">{day.date}</div>
              <img src={day.iconUrl} className="weather-forecast-icon" alt="weather icon" />
              <div className="weather-forecast-temperatures">
                <div className="weather-forecast-temperature"><strong>{day.maxTemp}º</strong></div>
                <div className="weather-forecast-temperature">{day.minTemp}º</div>
              </div>
            </div>
          ))}
        </div>
      </main>
      <footer>
        This project was coded by <a href="https://github.com/Isabelyoan/">Jo-anne Dlamini</a> and is hosted on <a href="https://github.com/Isabelyoan/Weatherapp">GitHub</a> and <a href="https://main--week7weatherapp.netlify.app/">Netlify</a>.
      </footer>
    </div>
  );
}

export default WeatherApp;
